import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

// Connect to the backend server
// Make sure this points to your deployed Render URL in production
const socket = io.connect(process.env.REACT_APP_BACKEND_URL || "http://localhost:3001");

function App() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [showChat, setShowChat] = useState(false);
  const [currentMessage, setCurrentMessage] = useState('');
  const [messageList, setMessageList] = useState([]);
  const chatBodyRef = useRef(null);

  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit('join_room', { username, room });
      setShowChat(true);
    }
  };

  const sendMessage = async () => {
    if (currentMessage.trim() !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time: new Date(Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
      };
      await socket.emit('send_message', messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    const handleReceiveMessage = (data) => {
      setMessageList((list) => [...list, data]);
    };
    socket.on('receive_message', handleReceiveMessage);

    // Cleanup function to remove the event listener
    return () => {
      socket.off('receive_message', handleReceiveMessage);
    };
  }, []);

  // Auto-scroll to the bottom when a new message is added
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messageList]);

  return (
    <div className="App">
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join A Chat Room</h3>
          <input
            type="text"
            placeholder="Your Name..."
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={(e) => { e.key === "Enter" && joinRoom(); }}
          />
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(e) => setRoom(e.target.value)}
            onKeyPress={(e) => { e.key === "Enter" && joinRoom(); }}
          />
          <button onClick={joinRoom}>Join Room</button>
        </div>
      ) : (
        <div className="chat-window">
          <div className="chat-header">
            <p>Live Chat - Room: {room}</p>
          </div>
          <div className="chat-body" ref={chatBodyRef}>
            {messageList.map((msg, index) => (
              <div
                key={index}
                className="message-container"
                id={username === msg.author ? "you" : "other"}
              >
                <div className="message">
                  <p>{msg.message}</p>
                  <div className="message-meta">
                    <span id="author">{msg.author}</span>
                    <span>{msg.time}</span>
                    {username === msg.author && <span className="tick">✓✓</span>}
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="chat-footer">
            <input
              type="text"
              value={currentMessage}
              placeholder="Type a message..."
              onChange={(e) => setCurrentMessage(e.target.value)}
              onKeyPress={(e) => { e.key === "Enter" && sendMessage(); }}
            />
            <button onClick={sendMessage}>&#9658;</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
